import React from 'react';
import { Redirect } from "react-router-dom";

export default class Checkout extends React.Component {
  constructor(props) {
    super(props);
    console.log(this.props.location.state.user);
    let token = document.cookie.split('; ').find(row => row.startsWith('token'));
    token = token.split('=')[1];


    var user;
    if (this.props.location.state === undefined) {
      user = "";
    } else {
      user = this.props.location.state.user;
    }
    let newItems = this.props.location.state.cart;
    let totalPrice = 0;
    for (let i = 0; i < newItems.length; i++) {
      newItems[i].totalPriceForItem = +newItems[i].price * +newItems[i].amountToBuy;
      totalPrice += newItems[i].totalPriceForItem;

      newItems[i].totalPriceForItem = Math.round(newItems[i].totalPriceForItem * 100) / 100;
      totalPrice = Math.round(totalPrice * 100) / 100;
    }

    this.state = {
      user: user,
      items: newItems,
      total: totalPrice,
      token:token,
    };

    this.onShop=this.onShop.bind(this);
    this.clearCart=this.clearCart.bind(this);
    this.calculateTotal=this.calculateTotal.bind(this);
    this.updateItemInCart=this.updateItemInCart.bind(this);
    this.removeFromCart=this.removeFromCart.bind(this);
    this.purchase=this.purchase.bind(this);
  }

  calculateTotal() {
    let newTotal = 0;
    for (let i = 0; i < this.state.items.length; i++) {
      newTotal += this.state.items[i].totalPriceForItem;
    }
    newTotal = Math.round(newTotal * 100) / 100;
    this.setState({ total: newTotal });
  }

  onShop() {
    this.setState({ shop: true });
  }

  clearCart() {
    this.setState({ items: [] });
    this.setState({ total: 0 });
  }

  updateItemInCart(idx, isDelivery) {
    let newItems = this.state.items;
    newItems[idx].totalPriceForItem = +newItems[idx].price * +newItems[idx].amountToBuy;
    if (isDelivery) {
      newItems[idx].totalPriceForItem = +newItems[idx].totalPriceForItem + +newItems[idx].deliveryFee;
    }
    newItems[idx].totalPriceForItem = Math.round(newItems[idx].totalPriceForItem * 100) / 100;
    this.setState( {items: newItems });
    this.calculateTotal();
  }

  removeFromCart(idx) {
    let newItems = this.state.items;
    newItems.splice(idx, 1);
    this.setState( {items: newItems });
    this.calculateTotal();
  }

  async deleteFood(seller, name) {
    await fetch(`http://localhost:8080/foodItems/${name}`, {
      method: 'DELETE',
      credentials: 'include',
      headers: {
        'Authorization': `Bearer ${this.state.token}`,
      },
    }).then(response => {
      // Do nothing
    })
    .catch(err => {
      throw new Error(err)
    })
  }

  async putFood(newItem) {
    await fetch('http://localhost:8080/foodItems', {
      method: 'PUT',
      credentials: 'include',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${this.state.token}`,
      },
      body: JSON.stringify(newItem)
    }).then(response => {
      // Do nothing
    })
    .catch(err => {
      throw new Error(err)
    })
  }

  async purchase() {
    if(this.state.items.length <= 0) {
      return;
    }
    for (let i = 0; i < this.state.items.length; i++) {
      let newItem = {seller: this.state.items[i].seller, name: this.state.items[i].name,
                    price: this.state.items[i].price, amount: this.state.items[i].quantity,
                    pickup: this.state.items[i].pickup, delivery: this.state.items[i].deliveryFee};
      if (newItem.amount <= 0) {
        await this.deleteFood(newItem.seller, newItem.name);
      } else {
        await this.putFood(newItem);
      }
      console.log(newItem);
      console.log(this.state.items[i]);

    }
    this.setState({ items: [] });
    this.setState({ total: 0 });
    alert("Thank you for your purchase!");
  }

  renderCards() {
    var cards = [];
    for (let i = 0; i < this.state.items.length; i++) {
      cards.push(<Card
              key={i}
              id={i}
              seller={this.state.items[i].seller}
              name={this.state.items[i].name}
              totalPriceForItem={this.state.items[i].totalPriceForItem}
              amountToBuy={this.state.items[i].amountToBuy}
              pickup={this.state.items[i].pickup}
              delivery={this.state.items[i].deliveryFee}
              updateItemInCart={this.updateItemInCart}
              removeFromCart={this.removeFromCart}
            />);
    }
    return cards;
  }

  render() {

    if (this.state.user === "") {
        return(
          <Redirect to="/" />
        );
    }

    if (this.state.shop) {
      return (
        <Redirect to={{ pathname: '/Shop', state: { user: this.state.user, cart: this.state.items} }} />
      );
    }

    return (
      <React.Fragment>
        <section className="content-container">

          <h2 className="pageheader">Total: ${this.state.total}</h2>
          <button className="selectButton" onClick={this.purchase}>
            Purchase
          </button>
          <button className="selectButton" onClick={this.onShop}>
            Return to shopping
          </button>
          <button className="selectButton" onClick={this.clearCart}>
            Clear Cart
          </button>
          <br />
          <br />

          {this.renderCards()}

          <br />

        </section>
      </React.Fragment>
    );
  }
}

class Card extends React.Component {
  constructor(props) {
    super(props);
    console.log(this.props);

    this.state = {
      amountToBuy: 0,
      isDelivery: false,
    };

    this.handleCheckboxChange=this.handleCheckboxChange.bind(this);
  }

  handleCheckboxChange(event) {
    const checked = event.target.checked;
    this.setState({
      [event.target.name]: checked
    });
    this.props.updateItemInCart(this.props.id, checked);
  }

  render () {
    return (
      <div className="itemColumns">
        <div className="itemValues" align="center">
          <p>Seller: {this.props.seller[0].name}</p>
          <p>Name: {this.props.name}</p>
          <p>Pickup: {this.props.pickup}</p>
          <p>Delivery: ${this.props.delivery}</p>
          <br />
          <p>Amount to buy: {this.props.amountToBuy}</p>
          <p>Total price: ${this.props.totalPriceForItem}</p>

          <button className="itemButton" onClick={() => {
            this.props.removeFromCart(this.props.id);
          }}>Remove from Cart</button>
          <input type="checkbox" id="delivery" name="isDelivery" checked={this.state.isDelivery} onChange={this.handleCheckboxChange} />
          <label htmlFor="delivery"> Delivery?</label>
        </div>
      </div>
    );
  }

}
