import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

function Card(props) {
  return (
    <div className="item">
      <div className="itemContent">
        <p>Seller: {props.seller}</p>
        <p>Name: {props.name}</p>
        <p>Price: {props.price}</p>
        <p>Amount: {props.amount}</p>
        <p>Pickup: {props.pickup}</p>
        <p>Delivery: {props.delivery}</p>
      </div>
      <div className="itemControls">
        <button className="itemButton" onClick={() => props.discard(props.id)}>Discard</button>
      </div>
    </div>
  );
}

class Display extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      id: 0,
      items: [],
      displayItem: 0,
      newSeller: "",
      newName: "",
      newPrice: "",
      newAmount: "",
      newPickup: "",
      newDelivery: "",
    };
    this.addCard=this.addCard.bind(this);
    this.handleTextChange=this.handleTextChange.bind(this);
    this.discard=this.discard.bind(this);
    this.addTestingValues=this.addTestingValues.bind(this);
  }

  addCard(event) {
    event.preventDefault();

    //Add new item
    let newItems = this.state.items;
    newItems.push({id: this.state.id, seller: this.state.newSeller, name: this.state.newName,
                  price: this.state.newPrice, amount: this.state.newAmount,
                  pickup: this.state.newPickup, delivery: this.state.newDelivery});
    this.setState({items: newItems});
    this.setState({id: this.state.id + 1});

    //Reset input boxes
    this.setState({newSeller: ""});
    this.setState({newName: ""});
    this.setState({newPrice: ""});
    this.setState({newAmount: ""});
    this.setState({newPickup: ""});
    this.setState({newDelivery: ""});
  }

  handleTextChange(event) {
    const value = event.target.value;
    this.setState({
      [event.target.name]: value
    });
  }

  discard(deleteId) {
    console.log(deleteId);
    let deleteIndex;
    for (deleteIndex = 0; deleteIndex < this.state.items.length; deleteIndex++) {
      if (this.state.items[deleteIndex].id == deleteId) {
        break;
      }
    }
    let newItems = this.state.items;
    newItems.splice(deleteIndex, 1);
    this.setState({items: newItems});
  }

  addTestingValues() {
    let newItems = this.state.items;
    let temp = this.state.id;
    newItems.push({id: temp++, seller: "Inc", name: "Tomatoes", price: 1.23, amount: 15, pickup: "112 Foods Inc Dr", delivery: 5.99});
    newItems.push({id: temp++, seller: "Inc", name: "Cabbages", price: 0.78, amount: 9, pickup: "112 Foods Inc Dr", delivery: 5.99});
    newItems.push({id: temp++, seller: "Ultimate", name: "Pasta", price: 3.45, amount: 2, pickup: "Ultimate Avenue", delivery: "Free"});
    newItems.push({id: temp++, seller: "Food Lion", name: "Mushrooms", price: 2, amount: 10, pickup: "Avent Ferry Dr", delivery: "2.99"});
    newItems.push({id: temp++, seller: "Lowes' Foods", name: "Bananas", price: 1.50, amount: 50, pickup: "NC State Campus Dr", delivery: "5.99"});
    this.setState({items: newItems});
    this.setState({id: temp});
  }

  renderCards() {
    var cards = [];
    for (let i = 0; i < this.state.items.length; i++) {
      cards.push(<Card
              id={this.state.items[i].id}
              seller={this.state.items[i].seller}
              name={this.state.items[i].name}
              price={this.state.items[i].price}
              amount={this.state.items[i].amount}
              pickup={this.state.items[i].pickup}
              delivery={this.state.items[i].delivery}
              complete={this.complete}
              discard={this.discard}
              shuffle={this.shuffle}
            />);
    }
    return cards;
  }

  renderForm() {
    return (
      <div>
        <h2>Add a food (temporarily on this page)</h2>
        <div class="item">
          <form onSubmit={this.addCard}>
            Seller: <input type="text" name="newSeller" value={this.state.newSeller} onChange={this.handleTextChange}></input>
            <br />
            Food name: <input type="text" name="newName" value={this.state.newName} onChange={this.handleTextChange}></input>
            <br />
            Price: <input type="text" name="newPrice" value={this.state.newPrice} onChange={this.handleTextChange}></input>
            <br />
            Amount: <input type="number" name="newAmount" value={this.state.newAmount} onChange={this.handleTextChange}></input>
            <br />
            Pickup address: <input type="text" name="newPickup" value={this.state.newPickup} onChange={this.handleTextChange}></input>
            <br />
            Delivery fee: <input type="text" name="newDelivery" value={this.state.newDelivery} onChange={this.handleTextChange}></input>
            <br />
            <button id="addItemButton">
              Add food
            </button>
          </form>
        </div>
      </div>
    );
  }

  render() {
    return (
      <div>
        {this.renderCards()}

        <br />

        {this.renderForm()}

        <br />
        <button id="addTestingValues" onClick={this.addTestingValues}>
          Populate with testing items
        </button>
      </div>
    );
  }
}

// ========================================

ReactDOM.render(
  <Display />,
  document.getElementById('root')
);
