import React from 'react';
import { Redirect } from "react-router-dom";

import '@fortawesome/fontawesome-free/css/all.min.css'; 
import 'bootstrap-css-only/css/bootstrap.min.css'; 
import './FoodItems.css'

class Card extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      amountToBuy: 0,
      isDelivery: false,
    };
    this.handleTextChange=this.handleTextChange.bind(this);
  }

  handleTextChange(event) {
    const value = event.target.value;
    this.setState({
      [event.target.name]: value
    });
  }

  render () {
    return (
      <div className="itemColumns">
        <div className="itemValues" align="center">
          <p>Seller: {this.props.seller}</p>
          <p>Name: {this.props.name}</p>
          <p>Price: ${this.props.price}</p>
          <p>Amount: {this.props.amount}</p>
          <p>Pickup: {this.props.pickup}</p>
          <p>Delivery: ${this.props.delivery}</p>

          <button className="itemButton" onClick={() => {
            this.props.addToCart(this.props.id, this.state.amountToBuy, this.state.isDelivery);
            this.setState({ amountToBuy: 0 });
          }}>Add to Cart</button>
          Amount: <input type="number" className="amountInput" name="amountToBuy" min='0' max={this.props.amount} value={this.state.amountToBuy} onChange={this.handleTextChange}></input>
        </div>
      </div>
    );
  }

}

export default class Shop extends React.Component {
  constructor(props) {
    super(props);

    var user;
    if (this.props.location.state === undefined) {
      user = "";
    } else {
      user = this.props.location.state.user;
    }

    this.state = {
      checkout: false,
      user: user,
      id: 0,
      items: [],
      cart: [],
      displayItem: 0,
      newSeller: "",
      newName: "",
      newPrice: "",
      newAmount: "",
      newPickup: "",
      newDelivery: "",
    };
    this.addFood=this.addFood.bind(this);
    this.handleTextChange=this.handleTextChange.bind(this);
    this.addToCart=this.addToCart.bind(this);
    this.onCheckout=this.onCheckout.bind(this);

    this.getFoodItems();

  }

  async getFoodItems(){
    await fetch('http://localhost:8080/foodItems', {
      method: 'GET',
    })
    .then(response => response.json())
    .then(responseJSON => {
      let newItems = responseJSON;
      for (let i = 0; i < newItems.length; i++) {
        newItems[i].amountToBuy = 0;
        for (let j = 0; j < this.props.location.state.cart.length; j++) {
          if (newItems[i].seller === this.props.location.state.cart[j].seller && newItems[i].name === this.props.location.state.cart[j].name) {
            newItems[i].amountToBuy = this.props.location.state.cart[j].amountToBuy;
            newItems[i].amount = +newItems[i].amount - +newItems[i].amountToBuy;
          }
        }
      }
      this.setState({items: newItems});
    })
    .catch(err => {
      throw new Error(err)
    })
  }

  async postFood(newItem) {
    await fetch('http://localhost:8080/foodItem', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(newItem)
    })
    .then(() => this.getFoodItems())
    .catch(err => {
      throw new Error(err)
    })
  }

  async deleteFood(seller, name) {
    await fetch(`http://localhost:8080/foodItem/${seller}/${name}`, {
      method: 'DELETE',
    })
    .then(() => this.getFoodItems())
    .catch(err => {
      throw new Error(err)
    })
  }

  addFood(event) {
    event.preventDefault();

    let newItem = {seller: this.state.newSeller, name: this.state.newName,
                  price: this.state.newPrice, amount: this.state.newAmount,
                  pickup: this.state.newPickup, delivery: this.state.newDelivery};

    this.postFood(newItem);

    //Reset input boxes
    this.setState({newSeller: ""});
    this.setState({newName: ""});
    this.setState({newPrice: ""});
    this.setState({newAmount: ""});
    this.setState({newPickup: ""});
    this.setState({newDelivery: ""});
  }

  handleTextChange(event) {
    const value = event.target.value;
    this.setState({
      [event.target.name]: value
    });
  }

  discard(seller, name) {
    this.deleteFood(seller, name);
  }

  addToCart(idx, amountToBuy, isDelivery) {
    if (amountToBuy > 0) {
      let newItems = this.state.items;
      newItems[idx].amountToBuy = +newItems[idx].amountToBuy + +amountToBuy;
      newItems[idx].amount = +newItems[idx].amount - +amountToBuy;
      newItems[idx].isDelivery = isDelivery;
      this.setState( {items: newItems });
    }
  }

  onCheckout() {
    let newCart = [];
    for (let i = 0; i < this.state.items.length; i++) {
      if (this.state.items[i].amountToBuy > 0) {
        newCart.push(this.state.items[i]);
      }
    }
    this.setState({ cart: newCart });

    this.setState({ checkout: true });
  }

  renderCards() {
    if (this.state.items.length <= 0) {
      return (
        <h2>No items in the store, sorry!</h2>
      );
    }
    var cards = [];
    for (let i = 0; i < this.state.items.length; i++) {
      cards.push(<Card
              key={i}
              id={i}
              seller={this.state.items[i].seller}
              name={this.state.items[i].name}
              price={this.state.items[i].price}
              amount={this.state.items[i].amount}
              pickup={this.state.items[i].pickup}
              delivery={this.state.items[i].delivery}
              addToCart={this.addToCart}
            />);
    }
    return cards;
  }

  render() {
    if (this.state.user === "") {
        return(
          <Redirect to="/" />
        );
    }

    if (this.state.checkout) {
      return (
        <Redirect to={{ pathname: '/Checkout', state: { user: this.state.user, cart: this.state.cart} }} />
      );
    }

    return (
      <React.Fragment>
        <section className="content-container">
          <h1 className="pageheader">Shop</h1>
          <button className="selectButton" onClick={this.onCheckout}>
            View Cart
          </button>
          <br />
          <br />
          {this.renderCards()}

          <br />

        </section>
      </React.Fragment>
    );
  }
}
